module.exports = function () {
    var port = '9001';
    this.ip  = undefined;
    this.express = undefined;

    this.start = function () {
        var env = this.express.get('env');
        var ip = this.ip;
        var port = this.port;

        this.express.listen(this.port, this.ip, function() {
            console.log('node-swagger-ui server listening on %s:%d, in %s mode', ip, port, env);
        });
    };
};